            totalPayment: -paymentValue,
            netwin:       -netwinDec,
          },
        },
        { new: true }
      );
  
      // 5) Si el summary quedó en cero (o no existe), podrías borrarlo:
      // const shouldDelete = summaryUpdated &&
      //   summaryUpdated.totalCash === 0 &&
      //   summaryUpdated.totalCredit === 0 &&
      //   summaryUpdated.totalDollars === 0 &&
