      return res.status(200).json({
        message: "Sale deleted",
        summary: summaryUpdated,
      });
  
    } catch (error) {
      console.error(error);
      return res
        .status(500)
        .json({ error: "Error al eliminar la venta", details: error.message });
    }
  };

